            }
        };

        if (submitted) {
            fetchData();
        }
    }, [submitted, formData.location]); // Add dependencies to the useEffect

    const Formd = () => {
        return (
            <>
                <div className="p-5">
                    <h2 className="text-[#00df9a] md:text-6xl text-center font-tit">Find your dream home</h2>
                </div>
                <div className="w-full border-2 flex items-center">
                    <form className="flex-col margin items-center p-3" onSubmit={handleSubmit}>
                        <label className="text-[#00df9a]">City :  </label>
                        <input type="text" className="bg-[#F7FFF7] rounded" name="location" value={formData.location} onChange={handleChange}></input>
                        <br />
                        <input className="text-[#00df9a] rounded " type="submit" value="search"></input>
                        <br />
                    </form>
                </div>
            </>
        );
    };

    return (
        <>
            {submitted ? <Display value={finalRes} /> : <Formd />}
        </>
    );
}

export default Search;
